// チェッカー
function setup() {
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      if (i+j % 2 == 1){
        fill(255);
        rect(i,j,size,size);
      }
      else{
        fill(150);
        rect(i,j,size,size);
      }
  }
}



// BLANK[1] ヒント： rectのx座標は size * i, y座標は size * j

