function setup(){
  createCanvas(270,180);
  background(0,0,100);
  stroke(255);
  strokeWeight(30);
  line(0, 0, width, height);
  line(height, 0, 0, height);

  const c = height / 30;
  const d = width / 60;
  const red = color(230,0,0);




  for(let i=0; i<30; i++){
    for(let j=0; j<60; j++){
      noStroke();
      if(9<=i && i<=20){
        fill(255);
        rect(0,i*c,width,c);
      }
      if(24<=j && j<=35){
        fill(255);
        rect(j*d,0,d,height);
      }
    }
  }


  // if(11<=i && i<=18){
  //     fill(red);
  //     rect(0,i*c,width,c);
  //   }


}

