// チェッカー
function setup(){
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      if ((i+j)% 2 == 0){
        fill(255);
        rect(size*i,size*j,size,size);
      }
      else{
        fill(120);
        rect(size*i,size*j,size,size);
      }
    }
    for(let j = 0; j < 3; j++){
      if ((i+j)% 2 == 1){
        fill(255,0,0);
        ellipse(size*i+size/2,size*j+size/2,size*0.8,size*0.8);
      }
    }

    for(let j = 5; j < 8; j++){
      if ((i+j)% 2 == 1){
        fill(0);
        ellipse(size*i+size/2,size*j+size/2,size*0.8,size*0.8);
      }
    }
  }
}


