// 心臓の鼓動のようなアニメーション
const cycle = 100; // 1周期のフレーム数
let count; // 何フレーム目か

function setup(){
  createCanvas(200, 200);
  count = 0;
}

function draw(){
  background(160, 192, 255);

  let speed = 1; // アニメーションの速さ
  // BLANK[2]
  count = (count + speed) % cycle;

  let size = 50;
  if(count<cycle/2){
    size = size++ ;
  }
  else{
    size = size-- ;
  }
  ellipse(width / 2, height / 2, size);
}

