// 吹き出し
function setup(){
  createCanvas(400, 400);
  textSize(16);
}

function draw(){
  background(220);
  balloon("関数は難しい？", 100, 100);
  balloon("関数は便利？", mouseX, mouseY);
}

function balloon(t, x, y){
  const w = textWidth(t); // テキストの幅
  const h = textAscent() + textDescent(); // テキストの高さ
  const p = 4; // 余白の大きさ (padding)

  push();

  // BLANK[1] 吹き出しの背景を先に描く
  fill(0,20,180);
  ellipse(x,y,w,h);

  // BLANK[2] 吹き出しの三角形を描く
  const tx1 =  fw + cos(60) * r1;
  const ty1 =  fh + r2 * -sin(60);
  const tx2 =  fw + r1 * cos(30);
  const ty2 =  fh + r2 * -sin(30);
  triangle(tx1,ty1,x+w*1.5,y+h*3,tx2,ty2);

  // 吹き出しのテキストを次に描く
  textAlign(LEFT, CENTER);
  fill(255);
  text(t, x + p, y + h / 2 + p);

  pop();
}

