  const w = textWidth(t); // テキストの幅
  const h = textAscent() + textDescent(); // テキストの高さ
  const p = 4; // 余白の大きさ (padding)

  push();

  // BLANK[1] 吹き出しの背景を先に描く
  fill(0,20,180);
  rect(x,y,w,h);

  // BLANK[2] 吹き出しの三角形を描く
  const x1 = x+(w/2);
  const x2 = x+(w*3/4);
  const x2 = x+(w*3/5);
  triangle(x1,h,x+w*1.5,y+h*3,x2,h);

  // 吹き出しのテキストを次に描く
  textAlign(LEFT, CENTER);
  fill(255);
  text(t, x + p, y + h / 2 + p);

  pop();
}

