  // let pastdays = (y-1970)* daysInYear + (m-1)* daysInMonth + (d-1);
  // 閏年の計算方法は？
  let pastdays = 0 
  for(i=1970; i == y; i++){
    pastdays = pastdays + daysInYear(i);
  }
  pastdays = pastdays + dayOfYear(y,m,d);

  if(pastdays % 7 == 0){return 2;}
  if(pastdays % 7 == 1){return 3;}
  if(pastdays % 7 == 2){return 4;}
  if(pastdays % 7 == 3){return 5;}
  if(pastdays % 7 == 4){return 6;}
  if(pastdays % 7 == 5){return 0;}
  if(pastdays % 7 == 6){return 1;}
}

function dayOfWeekAsString(dow){
  const a = ["日", "月", "火", "水", "木", "金", "土"];
  return a[dow];
}

