  const groundY = height * 0.7;
  fill(64, 192, 64);
  rect(0, groundY, width, height - groundY);

  const size = height*0.1

  if(keyIsDown(LEFT_ARROW)){x -= 10;}
  if(keyIsDown(RIGHT_ARROW)){x += 10;}
  x = constrain(x,size/2,width-(size/2));
  y = constrain(y,0,groundY-size);

  x += vx;
  y += vy;
  vy += g;
  if(y >= groundY-size){ 
    if(keyIsDown(" ".charCodeAt(0))){
      if(y <= height*0.2){
        vy = g*vy;
      }
      else
        {vy = -2*g * vy; 
      }
    }
  }
  else{vy += g;}

  vx = constrain(vx, -20, 20);
  vy = constrain(vy, -20, 20);
  x += vx;
  y += vy;


  fill(0);
  ellipse(x, y, size, size);


  evx=0;
  evy=0;
  for(let i = 0; i < enemies.length; i++){
    let t = enemies[i];
    fill(0);
    ellipse(width, random(0,groundY), random(5,10));
    t.x += t.evx;
    t.y += t.evy;
  }
  if(frameCount % 20 === 0) { // 20フレームごとに新しい的を追加する
    let t = {x:width/2, y:height/2, vx:random(-5,5), vy:random(-5,5), size:10};
    enemies.push(t);
  }

}

