  if(keyIsDown(LEFT_ARROW)){x -= 10;}
  if(keyIsDown(RIGHT_ARROW)){x += 10;}
  x = constrain(x,size/2,width-(size/2));
  y = constrain(y,0,groundY-size);

  x += vx;
  y += vy;
  vy += g;
  if(y >= groundY-size){ 
    if(keyIsDown(" ".charCodeAt(0))){
      if(y <= height*0.2){
        vy = g*vy;
      }
      else
        {vy = -2*g * vy; 
      }
    }
  }
  else{vy += g;}

  vx = constrain(vx, -20, 20);
  vy = constrain(vy, -20, 20);
  x += vx;
  y += vy;


  fill(220,0,0);
