// カレンダーを描画しよう
function setup(){
  createCanvas(200, 200);
  drawCalendar(2026, 1);
}

function drawCalendar(y, m){

  for(let i = 0; i < 7; i++){
    const x = i * width / 7;
    const y = 20;
    stroke(255);
    text(dayOfWeekAsString(i), x, y);
