// 最終課題を制作しよう

let x, y;
let vx, vy;
const g = 1;
let enemies;
let stars;
let s =0;



function setup(){
  createCanvas(windowWidth,windowHeight);
  vx=0;
  vy=0;
  x=width*0.3;
  y=height*0.5;
  enemies = [];
  stars = [];
  supers = [];
  fill(0);
  ellipse();

}

function windowResized(){
  resizeCanvas(windowWidth, windowHeight);
}


function draw(){
  background(160,192,255);
  const groundY = height * 0.7;
  fill(64, 192, 64);
  rect(0, groundY, width, height - groundY);


  const size = height*0.1

  if(keyIsDown(LEFT_ARROW)){x -= 10;}
  if(keyIsDown(RIGHT_ARROW)){x += 10;}
  x = constrain(x,size/2,width-(size/2));
  y = constrain(y,0,groundY-(size/2));

  x += vx;
  y += vy;
  vy += g;
  if(y > groundY-size){ 
    if(keyIsDown(" ".charCodeAt(0))){
      if(y <= height*0.2){
        vy = g*vy;
      }
      else
        {vy = -2*g * vy; 
      }
    }
  }
  else{vy += g;}

  vx = constrain(vx, -20, 20);
  vy = constrain(vy, -20, 20);
  x += vx;
  y += vy;


  fill(220,0,0);
  ellipse(x, y, size, size);




  for(let i=0; i<enemies.length; i++){
    let e = enemies[i];
    fill(0);
    ellipse(e.x,e.y,e.size);
    e.x -= e.vx;
  }
  if(frameCount % 40 === 0){
    let e = {x:width,y:random(height*0.3,groundY-15),vx:7,vy:0,size:30};
    enemies.push(e);
  }

  const activeEnemies = [];
  for(let i = 0; i < enemies.length; i++){
    let e = enemies[i];
    let hit = false;
    const distance = dist(x,y,e.x,e.y);
    const radian = (size+e.size)/2;
    if(distance<radian){
      hit=true;
    }
    if(hit){s--;}
    if(!hit){activeEnemies.push(e);}
  }
  enemies = activeEnemies;





  for(let i=0; i<stars.length; i++){
    let st = stars[i];
    fill(250,250,0);
    ellipse(st.x,st.y,st.size);
    st.x -= st.vx;
  }
  if(frameCount % 40 === 0){
    let st = {x:width,y:random(height*0.3,groundY-15),vx:9,vy:0,size:30};
    stars.push(st);
  }

  const activeStars = [];
  for(let i = 0; i < stars.length; i++){
    let st = stars[i];
    let hit = false;
    const distance = dist(x,y,st.x,st.y);
    const radian = (size+st.size)/2;
    if(distance<radian){
      hit=true;
    }
    if(hit){s++;}
    if(!hit){activeStars.push(st);}
  }
  stars = activeStars;





  for(let i=0; i<supers.length; i++){
    let e = supers[i];
    fill(0,0,230);
    ellipse(e.x,e.y,e.size);
    e.x -= e.vx;
  }
  if(frameCount % 80 === 0){
    let e = {x:width,y:random(height*0.3,groundY),vx:20,vy:0,size:10};
    supers.push(e);
  }

  const activeSupers = [];
  for(let i = 0; i < supers.length; i++){
    let e = supers[i];
    let hit = false;
    const distance = dist(x,y,e.x,e.y);
    const radian = (size+e.size)/2;
    if(distance<radian){
      hit=true;
    }
    if(hit){s += 3;}
    if(!hit){activeSupers.push(e);}
  }
  supers = activeSupers;





  score(s,width*0.1,height*0.9);

  function score(s,x,y){
    push();
    textSize(50);
    textAlign(LEFT, CENTER);
    fill(255);
    text(s,x,y);
    pop();
  }


}

